.header-container {
  padding: 20px 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.menu-container {
  @media (max-width: $tablet-max) {
    position: fixed;
    width: 350px;
    height: 100%;
    top: 0;
    right: 0;
    background: #0b0b15;
    z-index: 9;
    display: none;
    &.open {
      display: block;
    }
  }
  .btn {
    @media (min-width: $desktop) {
      margin-left: 35px;
    }
    @media (max-width: $tablet-max) {
      display: block;
    }
  }
  & > span {
    @media (max-width: $tablet-max) {
      padding: 0 22px;
      display: block;
    }
  }
}
.menu {
  position: relative;
  display: block;
  @media (min-width: $desktop) {
    display: inline-block;
  }
  li {
    @media (min-width: $desktop) {
      display: inline-block;
    }
    a {
      color: white;
      text-decoration: none;
      font-size: 16px;
      font-weight: bold;
      padding: 16px 0;
      display: block;
      opacity: 0.4;
      @media (min-width: $desktop) {
        display: inline-block;
        padding: 10px 15px;
      }
      &.router-link-active {
        opacity: 1;
      }
    }
    @media (min-width: $desktop) {
      &.line {
        @include bg-gradient($dark-orange, $light-orange);
        position: absolute;
        width: 18px;
        height: 3px;
        bottom: 0;
        border-radius: 50px;
        left: 15px;
        transform: translate3d(0, 0, 0);
        transition: all 500ms cubic-bezier(0.175, 0.885, 0.32, 1.275);
      }
    }
  }
  @media (max-width: $tablet-max) {
    padding: 100px 40px 60px 40px;
  }
}
.burger-menu {
  @media (min-width: $desktop) {
    display: none;
  }
}
.mobile-overlay {
  display: none;
  @media (max-width: $tablet-max) {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    background: black;
    opacity: 0.3;
    left: 0;
    top: 0;
  }
}
</style>
