<script>
import { computed, ref, toRef, watch, nextTick, onBeforeMount } from "vue";
import { useStore } from "vuex";
import moment from "moment";
export default {
  props: {
    isStartingCoin: Boolean,
    isinProgressCoin: Boolean,
  },
  setup() {
    const store = useStore();
    const settings = toRef(store.state, "settings");
    const now = ref("");
    let updateTime = setInterval(
      () => (now.value = Math.floor(new Date().getTime())),
      1000
    );

    const formattedTime = computed(() => {
      const roundStartTime = settings.value.roundstarttime - now.value;
      const roundEndTime = settings.value.roundendtime - now.value;
      store.commit("roundStartTimeEnded", roundStartTime < 0);
      store.commit("roundEndTimeEnded", roundEndTime < 0);
      let timeToConsider = roundStartTime;
      if (settings.value.enabledcoin && settings.value.starttimeunknown) {
        return {
          days: "?",
          hours: "??",
          minutes: "??",
          seconds: "??",
        };
      }
      if (
        !settings.value.enabledcoin ||
        (roundStartTime < 0 && roundEndTime < 0)
      ) {
        return {
          days: "-",
          hours: "--",
          minutes: "--",
          seconds: "--",
        };
      }

      if (roundStartTime < 0 && roundEndTime > 0) {
        timeToConsider = roundEndTime;
      }
      const splitTime = {
        days: moment.duration(timeToConsider).days(),
        hours: moment.duration(timeToConsider).hours().toLocaleString("en-US", {
          minimumIntegerDigits: 2,
          useGrouping: false,
        }),
        minutes: moment
          .duration(timeToConsider)
          .minutes()
          .toLocaleString("en-US", {
            minimumIntegerDigits: 2,
            useGrouping: false,
          }),
        seconds: moment
          .duration(timeToConsider)
          .seconds()
          .toLocaleString("en-US", {
            minimumIntegerDigits: 2,
            useGrouping: false,
          }),
      };
      return {
        days: splitTime.days,
        hours: splitTime.hours,
        minutes: splitTime.minutes,
        seconds: splitTime.seconds,
      };
    });
    // onBeforeMount(() => {});
    return { now, formattedTime };
  },
};
</script>
